@OptIn(ExperimentalFoundationApi::class)
@Composable
private fun LocationItem(
    location: SavedLocation,
    onClick: () -> Unit,
    onDelete: () -> Unit
) {
    var showDelete by remember { mutableStateOf(false) }
    val cardColor = getWeatherCardColor(location.weatherCondition)

    Card(
        modifier = Modifier
            .fillMaxWidth()
            .combinedClickable(
                onClick = {
                    if (showDelete) {
                        showDelete = false
                    } else {
                        onClick()
                    }
                },
                onLongClick = {
                    showDelete = !showDelete
                }
            ),
        shape = RoundedCornerShape(16.dp),
        colors = CardDefaults.cardColors(
            containerColor = cardColor
        )
    ) {
        Row(
            modifier = Modifier
                .fillMaxWidth()
                .padding(16.dp),
            horizontalArrangement = Arrangement.SpaceBetween,
            verticalAlignment = Alignment.CenterVertically
        ) {
            Row(
                horizontalArrangement = Arrangement.spacedBy(16.dp),
                verticalAlignment = Alignment.CenterVertically,
                modifier = Modifier.weight(1f)
            ) {
                // Weather Icon atau Pin Map untuk current location
                Box(
                    contentAlignment = Alignment.Center
                ) {
                    if (location.isCurrentLocation) {
                        // Pin map icon untuk lokasi user
                        Icon(
                            imageVector = Icons.Default.LocationOn,
                            contentDescription = "Current Location",
                            modifier = Modifier.size(32.dp),
                            tint = ColorGradient3
                        )
                    } else {
                        // Weather icon untuk lokasi lain
                        Image(
                            painter = painterResource(id = R.drawable.img_sun),
                            contentDescription = null,
                            modifier = Modifier.size(50.dp)
                        )
                    }
                }

                // Location Info
                Column {
                    Row(
                        verticalAlignment = Alignment.CenterVertically,
                        horizontalArrangement = Arrangement.spacedBy(4.dp),
                    ) {
                        Text(
                            text = location.name,
                            style = MaterialTheme.typography.titleMedium,
                            color = Color.White,
                            fontWeight = FontWeight.Bold
                        )
                        // Badge untuk current location
                        if (location.isCurrentLocation) {
                            Text(
                                text = "GPS",
                                style = MaterialTheme.typography.labelSmall,
                                color = Color.White,
                                modifier = Modifier
                                    .background(
                                        color = ColorGradient3,
                                        shape = RoundedCornerShape(4.dp)
                                    )
                                    .padding(horizontal = 6.dp, vertical = 2.dp),
                                fontSize = 10.sp
                            )
                        }
                    }
                    Text(
                        text = location.country,
                        style = MaterialTheme.typography.bodyMedium,
                        color = ColorTextSecondary
                    )
                    Text(
                        text = location.weatherCondition,
                        style = MaterialTheme.typography.bodySmall,
                        color = ColorTextSecondary
                    )
                }
            }

            // Temperature and Delete
            Row(
                horizontalArrangement = Arrangement.spacedBy(8.dp),
                verticalAlignment = Alignment.CenterVertically
            ) {
                Text(
                    text = "${location.temperature.toInt()}Â°",
                    style = MaterialTheme.typography.headlineMedium,
                    color = Color.White,
                    fontWeight = FontWeight.Bold
                )

                // Delete button hanya muncul saat long press
                if (showDelete) {
                    IconButton(onClick = {
                        onDelete()
                        showDelete = false
                    }) {
                        Icon(
                            Icons.Default.Delete,
                            contentDescription = "Delete",
                            tint = ColorTextSecondary
                        )
                    }
                }
            }
        }
    }
}

@OptIn(ExperimentalMaterial3Api::class)
@Composable
private fun AddLocationDialog(
    searchQuery: String,
    searchResults: List<SavedLocation>,
    isSearching: Boolean,
    onSearchQueryChange: (String) -> Unit,
    onLocationSelect: (SavedLocation) -> Unit,
    onDismiss: () -> Unit
) {
    Dialog(onDismissRequest = onDismiss) {
        Card(
            modifier = Modifier
                .fillMaxWidth()
                .heightIn(max = 600.dp),
            shape = RoundedCornerShape(24.dp),
            colors = CardDefaults.cardColors(
                containerColor = ColorSurface
            ),
            elevation = CardDefaults.cardElevation(defaultElevation = 8.dp)
        ) {
            Column(
                modifier = Modifier.fillMaxWidth()
            ) {
                // Header dengan Gradient
                Box(
                    modifier = Modifier
                        .fillMaxWidth()
                        .background(
                            brush = androidx.compose.ui.graphics.Brush.horizontalGradient(
                                colors = listOf(
                                    ColorGradient3,
                                    Color(0xFF5E72E4)
                                )
                            )
                        )
                        .padding(20.dp)
                ) {
                    Row(
                        modifier = Modifier.fillMaxWidth(),
                        horizontalArrangement = Arrangement.SpaceBetween,
                        verticalAlignment = Alignment.CenterVertically
                    ) {
                        Column {
                            Text(
                                text = "Add Location",
                                style = MaterialTheme.typography.headlineSmall,
                                color = Color.White,
                                fontWeight = FontWeight.Bold
                            )
                            Text(
                                text = "Search for cities worldwide",
                                style = MaterialTheme.typography.bodySmall,
                                color = Color.White.copy(alpha = 0.8f)
                            )
                        }
                        IconButton(
                            onClick = onDismiss,
                            modifier = Modifier
                                .background(
                                    color = Color.White.copy(alpha = 0.2f),
                                    shape = RoundedCornerShape(12.dp)
                                )
                        ) {
                            Icon(
                                Icons.Default.Close,
                                contentDescription = "Close",
                                tint = Color.White
                            )
                        }
                    }
                }

                Column(
                    modifier = Modifier
                        .fillMaxWidth()
                        .padding(20.dp),
                    verticalArrangement = Arrangement.spacedBy(16.dp)
                ) {
                    // Search Field dengan styling baru
                    OutlinedTextField(
                        value = searchQuery,
                        onValueChange = onSearchQueryChange,
                        modifier = Modifier
                            .fillMaxWidth(),
                        placeholder = {
                            Text(
                                "Search city name...",
                                color = ColorTextSecondary.copy(alpha = 0.6f)
                            )
                        },
                        leadingIcon = {
                            Icon(
                                Icons.Default.Search,
                                contentDescription = null,
                                tint = ColorGradient3,
                                modifier = Modifier.size(24.dp)
                            )
                        },
                        trailingIcon = {
                            if (searchQuery.isNotEmpty()) {
                                IconButton(onClick = { onSearchQueryChange("") }) {
                                    Icon(
                                        Icons.Default.Close,
                                        contentDescription = "Clear",
                                        tint = ColorTextSecondary,
                                        modifier = Modifier.size(20.dp)
                                    )
                                }
                            }
                        },
                        colors = OutlinedTextFieldDefaults.colors(
                            focusedTextColor = ColorTextPrimary,
                            unfocusedTextColor = ColorTextPrimary,
                            focusedBorderColor = ColorGradient3,
                            unfocusedBorderColor = ColorGradient3,
                            cursorColor = ColorGradient3
                        ),
                        shape = RoundedCornerShape(16.dp),
                        singleLine = true
                    )

                    // Info text
                    if (searchQuery.isEmpty()) {
                        Row(
                            horizontalArrangement = Arrangement.spacedBy(8.dp),
                            verticalAlignment = Alignment.CenterVertically,
                            modifier = Modifier.padding(horizontal = 4.dp)
                        ) {
                            Icon(
                                painter = painterResource(id = R.drawable.ic_location_pin),
                                contentDescription = null,
                                modifier = Modifier.size(16.dp),
                                tint = ColorTextPrimary.copy(alpha = 0.6f)
                            )
                            Text(
                                text = "Type at least 2 characters to search",
                                style = MaterialTheme.typography.bodySmall,
                                color = ColorTextPrimary.copy(alpha = 0.6f)
                            )
                        }
                    }

                    // Search Results
                    Box(
                        modifier = Modifier
                            .fillMaxWidth()
                            .heightIn(min = 200.dp, max = 350.dp)
                    ) {
                        when {
                            isSearching -> {
                                Box(
                                    modifier = Modifier.fillMaxSize(),
                                    contentAlignment = Alignment.Center
                                ) {
                                    Column(
                                        horizontalAlignment = Alignment.CenterHorizontally,
                                        verticalArrangement = Arrangement.spacedBy(12.dp)
                                    ) {
                                        CircularProgressIndicator(
                                            color = ColorGradient3,
                                            strokeWidth = 3.dp
                                        )
                                        Text(
                                            text = "Searching locations...",
                                            style = MaterialTheme.typography.bodyMedium,
                                            color = ColorTextPrimary.copy(alpha = 0.6f)
                                        )
                                    }
                                }
                            }
                            searchResults.isNotEmpty() -> {
                                LazyColumn(
                                    verticalArrangement = Arrangement.spacedBy(10.dp)
                                ) {
                                    items(searchResults) { location ->
                                        SearchResultItem(
                                            location = location,
                                            onClick = { onLocationSelect(location) }
                                        )
                                    }
                                }
                            }
                            searchQuery.length >= 2 -> {
                                Box(
                                    modifier = Modifier.fillMaxSize(),
                                    contentAlignment = Alignment.Center
                                ) {
                                    Column(
                                        horizontalAlignment = Alignment.CenterHorizontally,
                                        verticalArrangement = Arrangement.spacedBy(8.dp)
                                    ) {
                                        Icon(
                                            Icons.Default.Search,
                                            contentDescription = null,
                                            modifier = Modifier.size(48.dp),
                                            tint = ColorTextSecondary.copy(alpha = 0.3f)
                                        )
                                        Text(
                                            text = "No locations found",
                                            style = MaterialTheme.typography.bodyLarge,
                                            color = ColorTextSecondary,
                                            fontWeight = FontWeight.Medium
                                        )
                                        Text(
                                            text = "Try a different search term",
                                            style = MaterialTheme.typography.bodySmall,
                                            color = ColorTextSecondary.copy(alpha = 0.7f)
                                        )
                                    }
                                }
                            }
                            else -> {
                                Box(
                                    modifier = Modifier.fillMaxSize(),
                                    contentAlignment = Alignment.Center
                                ) {
                                    Column(
                                        horizontalAlignment = Alignment.CenterHorizontally,
                                        verticalArrangement = Arrangement.spacedBy(12.dp)
                                    ) {
                                        Icon(
                                            painter = painterResource(id = R.drawable.ic_location_pin),
                                            contentDescription = null,
                                            modifier = Modifier.size(64.dp),
                                            tint = ColorTextPrimary.copy(alpha = 0.3f)
                                        )
                                        Text(
                                            text = "Start typing to search",
                                            style = MaterialTheme.typography.bodyLarge,
                                            color = ColorTextPrimary.copy(alpha = 0.6f),
                                            fontWeight = FontWeight.Medium
                                        )
                                        Text(
                                            text = "Find cities from around the world",
                                            style = MaterialTheme.typography.bodySmall,
                                            color = ColorTextPrimary.copy(alpha = 0.6f)
                                        )
                                    }
                                }
                            }
                        }
                    }
                }
            }
        }
    }
}

@Composable
private fun SearchResultItem(
    location: SavedLocation,
    onClick: () -> Unit
) {
    val cardColor = getWeatherCardColor(location.weatherCondition)

    Card(
        modifier = Modifier
            .fillMaxWidth()
            .clickable(onClick = onClick),
        shape = RoundedCornerShape(16.dp),
        colors = CardDefaults.cardColors(
            containerColor = cardColor.copy(alpha = 0.15f)
        ),
        elevation = CardDefaults.cardElevation(
            defaultElevation = 0.dp,
            pressedElevation = 4.dp
        )
    ) {
        Row(
            modifier = Modifier
                .fillMaxWidth()
                .padding(16.dp),
            horizontalArrangement = Arrangement.SpaceBetween,
            verticalAlignment = Alignment.CenterVertically
        ) {
            Row(
                horizontalArrangement = Arrangement.spacedBy(12.dp),
                verticalAlignment = Alignment.CenterVertically,
                modifier = Modifier.weight(1f)
            ) {
                // Weather Icon dengan background
                Box(
                    modifier = Modifier
                        .size(48.dp)
                        .background(
                            color = cardColor.copy(alpha = 0.3f),
                            shape = RoundedCornerShape(12.dp)
                        ),
                    contentAlignment = Alignment.Center
                ) {
                    Image(
                        painter = painterResource(id = R.drawable.img_sun),
                        contentDescription = null,
                        modifier = Modifier.size(32.dp)
                    )
                }

                // Location info
                Column(
                    verticalArrangement = Arrangement.spacedBy(2.dp)
                ) {
                    Text(
                        text = location.name,
                        style = MaterialTheme.typography.bodyLarge,
                        color = ColorTextPrimary,
                        fontWeight = FontWeight.Bold
                    )
                    Row(
                        horizontalArrangement = Arrangement.spacedBy(6.dp),
                        verticalAlignment = Alignment.CenterVertically
                    ) {
                        Icon(
                            painter = painterResource(id = R.drawable.ic_location_pin),
                            contentDescription = null,
                            modifier = Modifier.size(14.dp),
                            tint = ColorTextPrimary.copy(alpha = 0.6f)
                        )
                        Text(
                            text = location.country,
                            style = MaterialTheme.typography.bodyMedium,
                            color = ColorTextPrimary.copy(alpha = 0.6f)
                        )
                    }
                    if (location.weatherCondition.isNotEmpty()) {
                        Text(
                            text = location.weatherCondition,
                            style = MaterialTheme.typography.bodySmall,
                            color = ColorTextPrimary.copy(alpha = 0.6f)
                        )
                    }
                }
            }

            // Temperature dengan styling
            Column(
                horizontalAlignment = Alignment.End,
                verticalArrangement = Arrangement.spacedBy(2.dp)
            ) {
                Text(
                    text = "${location.temperature.toInt()}Â°",
                    style = MaterialTheme.typography.headlineMedium,
                    color = ColorTextPrimary,
                    fontWeight = FontWeight.Bold
                )
                Box(
                    modifier = Modifier
                        .background(
                            color = ColorGradient3.copy(alpha = 0.2f),
                            shape = RoundedCornerShape(6.dp)
                        )
                        .padding(horizontal = 8.dp, vertical = 2.dp)
                ) {
                    Text(
                        text = "Add",
                        style = MaterialTheme.typography.labelSmall,
                        color = ColorGradient3,
                        fontWeight = FontWeight.Bold,
                        fontSize = 11.sp
                    )
                }
            }
        }
    }
}

package androidlead.weatherappui.util

import androidlead.weatherappui.R
import androidlead.weatherappui.ui.screen.util.WeatherTipItem

object WeatherCodeMapper {

    fun getWeatherTips(weatherCode: Int, temperature: Int, uvIndex: Int = 0, humidity: Int = 0): List<WeatherTipItem> {
        val tips = mutableListOf<WeatherTipItem>()

        // Temperature-based tips
        when {
            temperature >= 30 -> {
                tips.add(WeatherTipItem(R.drawable.ic_water_bottle, "Stay Hydrated", "Drink plenty of water"))
                tips.add(WeatherTipItem(R.drawable.ic_sunscreen, "Use Sunscreen", "Protect your skin"))
                tips.add(WeatherTipItem(R.drawable.ic_hat, "Wear Hat", "Protect from heat"))
                tips.add(WeatherTipItem(R.drawable.ic_sunglasses, "Wear Sunglasses", "Protect your eyes"))
            }
            temperature >= 20 -> {
                tips.add(WeatherTipItem(R.drawable.ic_sunglasses, "Wear Sunglasses", "Nice sunny day"))
                tips.add(WeatherTipItem(R.drawable.ic_water_bottle, "Stay Hydrated", "Keep drinking water"))
            }
            temperature >= 10 -> {
                tips.add(WeatherTipItem(R.drawable.ic_jacket, "Light Jacket", "Bring a light jacket"))
            }
            temperature < 10 -> {
                tips.add(WeatherTipItem(R.drawable.ic_warm_clothes, "Warm Clothes", "Dress warmly"))
                tips.add(WeatherTipItem(R.drawable.ic_jacket, "Winter Jacket", "It's quite cold"))
            }
        }

        // Weather condition-based tips
        when (weatherCode) {
            // Rain conditions
            51, 53, 55, 61, 63, 65, 80, 81, 82 -> {
                tips.add(WeatherTipItem(R.drawable.ic_umbrella, "Bring Umbrella", "Rain expected"))
                tips.add(WeatherTipItem(R.drawable.ic_jacket, "Waterproof Jacket", "Stay dry"))
            }
            // Thunderstorm
            95, 96, 99 -> {
                tips.add(WeatherTipItem(R.drawable.ic_umbrella, "Bring Umbrella", "Storm warning"))
                tips.add(WeatherTipItem(R.drawable.ic_jacket, "Stay Indoor", "Avoid outdoor activities"))
            }
            // Fog
            45, 48 -> {
                tips.add(WeatherTipItem(R.drawable.ic_wind, "Drive Carefully", "Low visibility"))
            }
            // Clear/Sunny
            0 -> {
                if (uvIndex > 5) {
                    tips.add(WeatherTipItem(R.drawable.ic_sunscreen, "Use Sunscreen", "High UV index"))
                }
            }
        }

        // Humidity-based tips
        if (humidity > 70) {
            tips.add(WeatherTipItem(R.drawable.ic_mosquito, "Mosquito Alert", "Use repellent"))
        }

        // Return at least 3 tips, add generic ones if needed
        if (tips.size < 3) {
            tips.add(WeatherTipItem(R.drawable.ic_water_bottle, "Stay Hydrated", "Drink water regularly"))
        }

        return tips.take(6) // Maximum 6 tips
    }

    fun getWeatherVideo(weatherCode: Int): Int {
        return when (weatherCode) {
            0 -> R.raw.video_forecast // Clear sky - use forecast video for sunny day
            1, 2, 3 -> R.raw.video_cloudy // Mainly clear, partly cloudy, and overcast
            45, 48 -> R.raw.video_cloudy // Fog
            51, 53, 55 -> R.raw.video_rain // Drizzle
            61, 63, 65 -> R.raw.video_rain // Rain
            71, 73, 75 -> R.raw.video_cloudy // Snow fall
            77 -> R.raw.video_cloudy // Snow grains
            80, 81, 82 -> R.raw.video_rain // Rain showers
            85, 86 -> R.raw.video_cloudy // Snow showers
            95 -> R.raw.video_storm // Thunderstorm
            96, 99 -> R.raw.video_storm // Thunderstorm with hail
            else -> R.raw.video_cloudy
        }
    }

    fun getWeatherIcon(weatherCode: Int): Int {
        return when (weatherCode) {
            0 -> R.drawable.img_sun // Clear sky
            1, 2, 3 -> R.drawable.img_clouds // Mainly clear, partly cloudy, and overcast
            45, 48 -> R.drawable.img_cloudy // Fog and depositing rime fog
            51, 53, 55 -> R.drawable.img_rain // Drizzle
            61, 63, 65 -> R.drawable.img_rain // Rain
            71, 73, 75 -> R.drawable.img_cloudy // Snow fall
            77 -> R.drawable.img_cloudy // Snow grains
            80, 81, 82 -> R.drawable.img_rain // Rain showers
            85, 86 -> R.drawable.img_cloudy // Snow showers
            95 -> R.drawable.img_thunder // Thunderstorm
            96, 99 -> R.drawable.img_thunder // Thunderstorm with hail
            else -> R.drawable.img_clouds
        }
    }

    fun getWeatherDescription(weatherCode: Int): String {
        return when (weatherCode) {
            0 -> "Clear sky"
            1 -> "Mainly clear"
            2 -> "Partly cloudy"
            3 -> "Overcast"
            45 -> "Foggy"
            48 -> "Depositing rime fog"
            51 -> "Light drizzle"
            53 -> "Moderate drizzle"
            55 -> "Dense drizzle"
            61 -> "Slight rain"
            63 -> "Moderate rain"
            65 -> "Heavy rain"
            71 -> "Slight snow fall"
            73 -> "Moderate snow fall"
            75 -> "Heavy snow fall"
            77 -> "Snow grains"
            80 -> "Slight rain showers"
            81 -> "Moderate rain showers"
            82 -> "Violent rain showers"
            85 -> "Slight snow showers"
            86 -> "Heavy snow showers"
            95 -> "Thunderstorm"
            96 -> "Thunderstorm with slight hail"
            99 -> "Thunderstorm with heavy hail"
            else -> "Unknown"
        }
    }
}

